import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import Navigation from './Navigation';
import SearchBar from './SearchBar';
import styles from './Header.module.css';
import { ShoppingCart, MapPin, User } from 'lucide-react';

const Header = () => {
  const [isScrolled, setIsScrolled] = useState(false);
  const [cartItemCount, setCartItemCount] = useState(0);

  useEffect(() => {
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 60);
    };
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <header className={`${styles.header} ${isScrolled ? styles.headerScrolled : ''}`}>
      <div className={styles.headerTop}>
        <div className={styles.container}>
          <Link to="/" className={styles.headerLogo}>
            <i className={styles.logoIcon}>PhoneTech</i>
          </Link>

          <SearchBar />

          <div className={styles.headerActions}>
            <Link to="/account" className={styles.profileLink}>
              <User size={20} />
              <span>Đăng nhập</span>
            </Link>

            <Link to="/cart" className={styles.cartLink}>
              <div className={styles.cartBox}>
                <ShoppingCart size={20} />
                {cartItemCount > 0 && <span className={styles.cartCount}>{cartItemCount}</span>}
              </div>
              <span>Giỏ hàng</span>
            </Link>

            <button className={styles.locationButton} onClick={() => {}}>
              <MapPin size={20} />
              <span>Chọn địa chỉ</span>
            </button>
          </div>
        </div>
      </div>

      <div className={styles.headerMain}>
        <div className={styles.container}>
          <Navigation />
        </div>
      </div>
    </header>
  );
};

export default Header;
