import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../../../contexts/AuthContext';
import Navigation from './Navigation';
import styles from './Header.module.css';

// Import icons
import { ShoppingCart, User, Menu, X, MapPin, Search } from 'lucide-react';

const Header = () => {
  const [isScrolled, setIsScrolled] = useState(false);
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');
  const { user, isAuthenticated, logout } = useAuth();

  // Kiểm tra scroll để thay đổi hiệu ứng của header
  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > 60) {
        setIsScrolled(true);
      } else {
        setIsScrolled(false);
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  // Toggle menu điện thoại
  const toggleMobileMenu = () => {
    setIsMobileMenuOpen(!isMobileMenuOpen);
    
    // Khi mở menu, ngăn scroll của body
    if (!isMobileMenuOpen) {
      document.body.style.overflow = 'hidden';
    } else {
      document.body.style.overflow = 'auto';
    }
  };

  const handleLogout = async () => {
    try {
      await logout();
      // Đóng mobile menu nếu đang mở
      if (isMobileMenuOpen) {
        toggleMobileMenu();
      }
    } catch (error) {
      console.error('Logout failed:', error);
    }
  };

  const handleSearchSubmit = (e) => {
    e.preventDefault();
    if (searchQuery.trim()) {
      // Implement search functionality
      console.log('Searching for:', searchQuery);
      // Navigate to search page
      // history.push(`/tim-kiem?key=${encodeURIComponent(searchQuery)}`);
    }
  };

  const openLocationPicker = () => {
    // Implement location picker functionality
    console.log('Opening location picker');
  };

  return (
    <header className={`${styles.header} ${styles.v2024} ${styles.hasbanner} ${isScrolled ? styles.sticky : ''}`}>
      <div className={styles.header__top}>
        <div className={styles.container}>
          {/* Logo */}
          <Link to="/" className={styles.header__logo} aria-label="logo">
            <i className={styles.logo}>PhoneTech</i>
          </Link>

          {/* Search Form */}
          <form 
            action="/tim-kiem" 
            className={styles.header__search}
            onSubmit={handleSearchSubmit}
          >
            <input 
              type="text" 
              className={styles.inputSearch} 
              placeholder="Bạn tìm gì..." 
              name="key" 
              autoComplete="off" 
              maxLength="100"
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
            />
            <button type="submit" aria-label="button suggest search">
              <Search className={styles.iconSearch} size={18} />
            </button>
            <div id="search-result" className={styles.searchResult}></div>
          </form>

          {/* User Profile */}
          <div className={styles.profile}>
            {isAuthenticated ? (
              <Link to="/profile" className={`${styles.nameOrder} ${styles.active}`}>
                <User size={16} />
                {user?.name || 'Tài khoản'}
              </Link>
            ) : (
              <Link to="/login" className={`${styles.nameOrder} ${styles.active}`}>
                <User size={16} />
                Đăng nhập
              </Link>
            )}
          </div>

          {/* Cart */}
          <Link to="/cart" className={`${styles.header__cart} ${styles.menuInfo}`} id="cart-box">
            <div className={styles.boxCart}>
              <ShoppingCart className={styles.iconCart} size={22} />
              <span className={styles.cartNumber}></span>
            </div>
            <span>Giỏ hàng</span>
          </Link>

          {/* Location */}
          <button className={styles.header__address} onClick={openLocationPicker}>
            <MapPin size={16} className={styles.iconLocation} />
            <span>Chọn vị trí để xem giá, thời gian giao:</span>
          </button>
        </div>
      </div>

      {/* Main Navigation */}
      <div className={styles.header__main}>
        <div className={styles.container}>
          <Navigation />
        </div>
      </div>

      {/* Mobile Menu Toggle & Mobile Menu */}
      <button
        className={styles.mobileMenuToggle}
        onClick={toggleMobileMenu}
        aria-label="Toggle menu"
      >
        {isMobileMenuOpen ? <X size={24} /> : <Menu size={24} />}
      </button>

      {isMobileMenuOpen && (
        <div className={styles.mobileMenu}>
          <div className={styles.mobileMenuHeader}>
            <form 
              action="/tim-kiem" 
              className={styles.mobileSearch}
              onSubmit={handleSearchSubmit}
            >
              <input 
                type="text" 
                placeholder="Bạn tìm gì..." 
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
              />
              <button type="submit">
                <Search size={18} />
              </button>
            </form>
          </div>
          <div className={styles.mobileMenuContent}>
            <Navigation mobile={true} onClose={toggleMobileMenu} />
            <div className={styles.mobileMenuDivider}></div>
            <div className={styles.mobileMenuLinks}>
              <Link to="/promotion" className={styles.mobileMenuLink} onClick={toggleMobileMenu}>
                Khuyến mãi
              </Link>
              <Link to="/store-location" className={styles.mobileMenuLink} onClick={toggleMobileMenu}>
                Hệ thống cửa hàng
              </Link>
              <Link to="/support" className={styles.mobileMenuLink} onClick={toggleMobileMenu}>
                Hỗ trợ
              </Link>
            </div>
            {!isAuthenticated ? (
              <div className={styles.mobileMenuAuth}>
                <Link to="/login" className={styles.mobileMenuAuthButton} onClick={toggleMobileMenu}>
                  Đăng nhập
                </Link>
                <Link to="/register" className={styles.mobileMenuAuthButtonOutline} onClick={toggleMobileMenu}>
                  Đăng ký
                </Link>
              </div>
            ) : (
              <div className={styles.mobileMenuUserLinks}>
                <Link to="/profile" className={styles.mobileMenuButton} onClick={toggleMobileMenu}>
                  Tài khoản của tôi
                </Link>
                <Link to="/orders" className={styles.mobileMenuButton} onClick={toggleMobileMenu}>
                  Đơn hàng
                </Link>
                <button onClick={handleLogout} className={styles.mobileMenuButton}>
                  Đăng xuất
                </button>
              </div>
            )}
          </div>
        </div>
      )}
    </header>
  );
};

export default Header;
