import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { ApiService } from '../../services/api';
import HeroBanner from './HeroBanner';
import FeaturedProducts from './FeaturedProducts';
import LoadingFallback from '../../components/common/LoadingFallback';
import styles from './Home.module.css';

const Home = () => {
  const [banners, setBanners] = useState([]);
  const [featuredProducts, setFeaturedProducts] = useState([]);
  const [phoneProducts, setPhoneProducts] = useState([]);
  const [laptopProducts, setLaptopProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchHomeData = async () => {
      setLoading(true);
      try {
        // Fetch banners, featured products, and hot phones in parallel with error handling
        const [bannersData, productsData, hotPhonesData] = await Promise.all([
          ApiService.fetchBanners().catch(err => {
            console.error('Error fetching banners:', err);
            return []; // Return empty array on error
          }),
          ApiService.fetchProducts().catch(err => {
            console.error('Error fetching products:', err);
            return []; // Return empty array on error
          }),
          ApiService.fetchHotPhones().catch(err => {
            console.error('Error fetching hot phones:', err);
            return []; // Return empty array on error
          }),
        ]);

        setBanners(bannersData);
        
        // Featured products (first 6)
        setFeaturedProducts(productsData.slice(0, 6));
        
        // Hot phones from API
        setPhoneProducts(hotPhonesData);
        
        // Laptop products
        setLaptopProducts(productsData.filter(p => p.categoryId === 'laptop').slice(0, 4));
      } catch (err) {
        console.error('Error fetching home data:', err);
        setError('Không thể tải dữ liệu. Vui lòng thử lại sau.');
      } finally {
        setLoading(false);
      }
    };

    fetchHomeData();
  }, []);

  if (loading) {
    return <LoadingFallback message="Đang tải dữ liệu trang chủ..." />;
  }

  if (error) {
    return (
      <div className={styles.errorContainer}>
        <i className="fas fa-exclamation-circle"></i>
        <p>{error}</p>
        <button 
          onClick={() => window.location.reload()}
          className={styles.refreshButton}
        >
          Tải lại trang
        </button>
      </div>
    );
  }

  return (
    <div className={styles.homePage}>
      {banners && banners.length > 0 && <HeroBanner banners={banners} />}
      
      {featuredProducts && featuredProducts.length > 0 && (
        <section className={styles.featuredSection}>
          <div className={styles.sectionHeader}>
            <h2 className={styles.sectionTitle}>Sản phẩm nổi bật</h2>
            <Link to="/products" className={styles.viewAllLink}>
              Xem tất cả <i className="fas fa-chevron-right"></i>
            </Link>
          </div>
          <FeaturedProducts products={featuredProducts} />
        </section>
      )}
      
      {phoneProducts && phoneProducts.length > 0 && (
        <section className={styles.categorySection}>
          <div className={styles.sectionHeader}>
            <h2 className={styles.sectionTitle}>Điện thoại hot</h2>
            <Link to="/phone" className={styles.viewAllLink}>
              Xem tất cả <i className="fas fa-chevron-right"></i>
            </Link>
          </div>
          <FeaturedProducts products={phoneProducts} />
        </section>
      )}
      
      {laptopProducts && laptopProducts.length > 0 && (
        <section className={styles.categorySection}>
          <div className={styles.sectionHeader}>
            <h2 className={styles.sectionTitle}>Laptop đáng mua</h2>
            <Link to="/laptop" className={styles.viewAllLink}>
              Xem tất cả <i className="fas fa-chevron-right"></i>
            </Link>
          </div>
          <FeaturedProducts products={laptopProducts} />
        </section>
      )}
    </div>
  );
};

export default Home;
