import { useContext } from 'react';
import { CartContext } from '../contexts/CartContext';

export const useCart = () => {
  const context = useContext(CartContext);
  
  if (!context) {
    throw new Error('useCart must be used within a CartProvider');
  }
  
  return {
    cart: context.cart || { items: [], total: 0, itemCount: 0 },
    loading: context.loading || false,
    error: context.error || null,
    addToCart: context.addToCart || (() => console.warn('addToCart not implemented')),
    updateCartItemQuantity: context.updateCartItemQuantity || (() => console.warn('updateCartItemQuantity not implemented')),
    removeFromCart: context.removeFromCart || (() => console.warn('removeFromCart not implemented')),
    clearCart: context.clearCart || (() => console.warn('clearCart not implemented'))
  };
};
